
//处理登录注册

//面向对象思想
//创建类
function Auth() {
    var self = this;
    self.maskWrapper = $('.mask-wrapper')  //整个模态框
    self.scrollWrapper = $('.scroll-wrapper');  //子模态框登录注册
    self.smsCaptcha = $('.sms-captcha-btn');
}
//监听短信发送成功以后的后续操作
Auth.prototype.listenSmsSuccessEvent = function(){
    var self = this;
    messageBox.showSuccess("短信验证码发送成功")
    self.smsCaptcha.addClass('disabled');
    var count = 10;
    self.smsCaptcha.unbind('click');
    var timer = setInterval(function () {
        self.smsCaptcha.text(count+'s');
        count -= 1;
        if(count <= 0){
            clearInterval(timer);
            self.smsCaptcha.removeClass('disabled');
            self.smsCaptcha.text('发送验证码');
            self.listenSmsCaptchaEvent();
        }
    },1000);

}


//定义类的方法
Auth.prototype.showEvent = function(){
    var self = this;
    self.maskWrapper.show()
}

Auth.prototype.hideEvent = function(){
    var self = this;
    self.maskWrapper.hide()
}
//监听显示隐藏的方法
Auth.prototype.listenShowHideEvent = function(){
    var self = this;
    var signinBtn = $('.signin-btn') //登录
    var signupBtn = $('.signup-btn') //注册
    var closeBtn = $('.close-btn') //关闭模态框

    signinBtn.click(function () {
        //显示模态框
        self.showEvent()
        self.scrollWrapper.css({'left':0})
    })
    signupBtn.click(function () {
        //显示模态框
        self.showEvent()
        self.scrollWrapper.css({'left':-400})
    })
    closeBtn.click(function () {
        self.hideEvent()
    })
}

//监听切换方法
Auth.prototype.listenSwitchEvent = function(){
    var self = this;
    var switcher = $('.switch')
    switcher.click(function () {
        var currentLeft = self.scrollWrapper.css("left"); //获取模态框距离左边的位置
        currentLeft = parseInt(currentLeft);
        if(currentLeft < 0){
           self.scrollWrapper.animate({"left":"0"});
        }else{
            self.scrollWrapper.animate({"left":"-400px"});
        }
    })
}

//监听登录方法
Auth.prototype.listenSigninEvent = function(){
    var self = this;
    var signinGroup = $('.signin-group')
    //获取三个输入框
    var telephoneInput = signinGroup.find("input[name='telephone']");
    var passwordInput = signinGroup.find("input[name='password']");
    var rememberInput = signinGroup.find("input[name='remember']");
    //获取提交按钮
    var submitBtn = signinGroup.find(".submit-btn")

    submitBtn.click(function () {
        //获取三个输入框的值
        var telephone = telephoneInput.val();
        var password = passwordInput.val();
        var remember = rememberInput.prop('checked');
        qfajax.post({
            'url':'/account/login/',
            'data':{
                'telephone':telephone,
                'password':password,
                'remember':remember?1:0,
            },
            'success':function (result) {
                self.hideEvent();
                window.location.reload();
            }
        })
    })
}

//监听图形验证码
Auth.prototype.listenImageCaptchaEvent = function(){
    var img_captcha = $('.img-captcha')
    img_captcha.click(function () {
        img_captcha.attr("src","/account/img_captcha/"+"?random="+Math.random())
    })
}


//监听短信验证码

Auth.prototype.listenSmsCaptchaEvent = function () {
    var self = this;
    var smsCaptcha = $(".sms-captcha-btn");
    var telephoneInput = $('.signup-group input[name="telephone"]');
    smsCaptcha.click(function () {
        var telephone = telephoneInput.val();
        if(!telephone){
           messageBox.showInfo("请输入手机号")
        }else{
            qfajax.get({
            'url': '/account/sms_captcha/',
            'data':{
                'telephone': telephone
            },
            'success': function (result) {
                if(result['code'] == 200){
                    self.listenSmsSuccessEvent();
                }
            },
            'fail': function (error) {
                console.log(error);
            }
        });
        }

    });
};

//监听注册方法

Auth.prototype.listenSignupEvent = function () {
    var signupGroup = $('.signup-group');
    var submitBtn = signupGroup.find('.submit-btn');
    submitBtn.click(function (event) {
        event.preventDefault();
        var telephoneInput = signupGroup.find("input[name='telephone']");
        var usernameInput = signupGroup.find("input[name='username']");
        var imgCaptchaInput = signupGroup.find("input[name='img_captcha']");
        var password1Input = signupGroup.find("input[name='password1']");
        var password2Input = signupGroup.find("input[name='password2']");
        var smsCaptchaInput = signupGroup.find("input[name='sms_captcha']");

        var telephone = telephoneInput.val();
        var username = usernameInput.val();
        var img_captcha = imgCaptchaInput.val();
        var password1 = password1Input.val();
        var password2 = password2Input.val();
        var sms_captcha = smsCaptchaInput.val();

        qfajax.post({
            'url': '/account/register/',
            'data': {
                'telephone': telephone,
                'username': username,
                'img_captcha': img_captcha,
                'password1': password1,
                'password2': password2,
                'sms_captcha': sms_captcha
            },
            'success': function (result) {
                if(result['code'] == 200){
                    messageBox.showSuccess('注册成功')
                }
                window.location.reload();
            }
        });
    });
};

//run() 统一执行类的方法
Auth.prototype.run = function(){
    var self = this;
    self.listenShowHideEvent();
    self.listenSwitchEvent();
    self.listenSigninEvent();
    self.listenImageCaptchaEvent();
    self.listenSmsCaptchaEvent();
    self.listenSignupEvent();
}

//实例化对象

$(function () {
    var auth = new Auth();
    auth.run();
})

$(function () {
    if(window.template){
        template.defaults.imports.TimeKangbazi =  function (dataValue) {
            var date = new Date(dataValue);
            var datets = date.getTime();//毫秒级别的
            var nowts = (new Date()).getTime();
            var timestamp = (nowts-datets)/1000;//秒
             if(timestamp < 60){
                 return '刚刚';
             }else if (timestamp>= 60 && timestamp < 60*60) {
                 minutes = parseInt(timestamp / 60)
                 return  minutes+'分钟前';
             }else if(timestamp>= 60*60 && timestamp < 60*60*24) {
                 hours = parseInt(timestamp / 60 / 60)
                 return hours+'小时前';
             }else if(timestamp>= 60*60*24 && timestamp < 60*60*24*30){
                days = parseInt(timestamp / 60 / 60 / 24)
                return days + '天前'
            }else{
                var year = date.getFullYear();
                var month = date.getMonth();
                var day = date.getDay();
                var hour = date.getHours();
                var minute =date.getMinutes();
                return year + '/'+month + '/'+day + '/'+hour+'/'+minute;

            }
        }
    }
})
//处理导航条
function FrontBase() {

}
FrontBase.prototype.listenAuthBoxHover = function(){
    var authBox = $(".auth-box");
    var useMoreBox = $(".user-more-box")
    authBox.hover(function () {
        useMoreBox.show();
    },function () {
        useMoreBox.hide();
    })
}

FrontBase.prototype.run = function () {
    var self = this;
    self.listenAuthBoxHover();
}

$(function () {
    var frontBase = new FrontBase()
    frontBase.run();
})